import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

  // API Ñ„ÑƒÐ½ÐºÑ†Ð¸Ñ
  const api = {
    login: (data) => fetch('/api/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(data)
    }).then(res => res.json())
  };



// Ð’Ñ…Ð¾Ð´
const LoginPage = () => {
  const [form, setForm] = useState({ login: '', password: '' });
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const result = await api.login(form);
    if (result.success) {
      localStorage.setItem('user', JSON.stringify(result.user));
      window.location.href = result.isAdmin ? '/admin' : '/orders';
    } else {
      setError(result.error);
    }
  };

  return (
    <div className="auth-page">
      <h2>Ð’Ñ…Ð¾Ð´</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="Ð›Ð¾Ð³Ð¸Ð½" value={form.login}
          onChange={e => setForm({...form, login: e.target.value})} />
        <input type="password" placeholder="ÐŸÐ°Ñ€Ð¾Ð»ÑŒ" value={form.password}
          onChange={e => setForm({...form, password: e.target.value})} />
        {error && <div className="error">{error}</div>}
        <button type="submit">Ð’Ð¾Ð¹Ñ‚Ð¸</button>
      </form>
      <Link to="/register">ÐÐµÑ‚ Ð°ÐºÐºÐ°ÑƒÐ½Ñ‚Ð°? Ð—Ð°Ñ€ÐµÐ³Ð¸ÑÑ‚Ñ€Ð¸Ñ€ÑƒÐ¹Ñ‚ÐµÑÑŒ</Link>
      <Link to="/">ÐÐ°Ð·Ð°Ð´</Link>
    </div>
  );
};

// Ð¡Ð¾Ð·Ð´Ð°Ð½Ð¸Ðµ Ð·Ð°ÑÐ²ÐºÐ¸ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
const OrdersPage = () => {
  const [user] = useState(() => {
    try {
      return JSON.parse(localStorage.getItem('user'));
    } catch {
      return null;
    }
  });
  
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [formStep, setFormStep] = useState(1);
  const [newOrder, setNewOrder] = useState({
    address: 'ÐÐ´Ñ€ÐµÑÑÑÑ', 
    contactPhone: '+7(912)-111-11-11', 
    serviceDate: '', 
    serviceTime: '10:00',
    serviceType: 'Ð¾Ð±Ñ‰Ð¸Ð¹ ÐºÐ»Ð¸Ð½Ð¸Ð½Ð³', 
    customService: '', 
    paymentType: 'Ð½Ð°Ð»Ð¸Ñ‡Ð½Ñ‹Ðµ',
    specialRequests: ''
  });

  const serviceOptions = [
    { value: 'Ð¾Ð±Ñ‰Ð¸Ð¹ ÐºÐ»Ð¸Ð½Ð¸Ð½Ð³', label: 'ðŸ§¹ ÐžÐ±Ñ‰Ð¸Ð¹ ÐºÐ»Ð¸Ð½Ð¸Ð½Ð³', description: 'Ð•Ð¶ÐµÐ´Ð½ÐµÐ²Ð½Ð°Ñ ÑƒÐ±Ð¾Ñ€ÐºÐ° Ð¿Ð¾Ð¼ÐµÑ‰ÐµÐ½Ð¸Ð¹' },
    { value: 'Ð³ÐµÐ½ÐµÑ€Ð°Ð»ÑŒÐ½Ð°Ñ ÑƒÐ±Ð¾Ñ€ÐºÐ°', label: 'âœ¨ Ð“ÐµÐ½ÐµÑ€Ð°Ð»ÑŒÐ½Ð°Ñ ÑƒÐ±Ð¾Ñ€ÐºÐ°', description: 'ÐŸÐ¾Ð»Ð½Ð°Ñ ÐºÐ¾Ð¼Ð¿Ð»ÐµÐºÑÐ½Ð°Ñ ÑƒÐ±Ð¾Ñ€ÐºÐ°' },
    { value: 'Ð¿Ð¾ÑÐ»ÐµÑÑ‚Ñ€Ð¾Ð¸Ñ‚ÐµÐ»ÑŒÐ½Ð°Ñ ÑƒÐ±Ð¾Ñ€ÐºÐ°', label: 'ðŸ—ï¸ ÐŸÐ¾ÑÐ»ÐµÑÑ‚Ñ€Ð¾Ð¸Ñ‚ÐµÐ»ÑŒÐ½Ð°Ñ ÑƒÐ±Ð¾Ñ€ÐºÐ°', description: 'Ð£Ð±Ð¾Ñ€ÐºÐ° Ð¿Ð¾ÑÐ»Ðµ Ñ€ÐµÐ¼Ð¾Ð½Ñ‚Ð°' },
    { value: 'Ñ…Ð¸Ð¼Ñ‡Ð¸ÑÑ‚ÐºÐ° ÐºÐ¾Ð²Ñ€Ð¾Ð² Ð¸ Ð¼ÐµÐ±ÐµÐ»Ð¸', label: 'ðŸ§¼ Ð¥Ð¸Ð¼Ñ‡Ð¸ÑÑ‚ÐºÐ° ÐºÐ¾Ð²Ñ€Ð¾Ð² Ð¸ Ð¼ÐµÐ±ÐµÐ»Ð¸', description: 'ÐŸÑ€Ð¾Ñ„ÐµÑÑÐ¸Ð¾Ð½Ð°Ð»ÑŒÐ½Ð°Ñ Ñ‡Ð¸ÑÑ‚ÐºÐ°' },
    { value: 'Ð¼Ð¾Ð¹ÐºÐ° Ð¾ÐºÐ¾Ð½', label: 'ðŸªŸ ÐœÐ¾Ð¹ÐºÐ° Ð¾ÐºÐ¾Ð½', description: 'Ð§Ð¸ÑÑ‚ÐºÐ° Ð¾ÐºÐ¾Ð½ Ð¸ Ð²Ð¸Ñ‚Ñ€Ð¸Ð½' },
    { value: 'Ð´Ñ€ÑƒÐ³Ð¾Ðµ', label: 'ðŸŽ¯ Ð”Ñ€ÑƒÐ³Ð°Ñ ÑƒÑÐ»ÑƒÐ³Ð°', description: 'Ð¡Ð¿ÐµÑ†Ð¸Ð°Ð»ÑŒÐ½Ñ‹Ðµ ÑƒÑÐ»ÑƒÐ³Ð¸' }
  ];

  const timeSlots = [
    '08:00', '09:00', '10:00', '11:00', '12:00', 
    '13:00', '14:00', '15:00', '16:00', '17:00', '18:00'
  ];

  useEffect(() => {
    if (user && user.id) {
      setLoading(true);
      api.getUserOrders(user.id)
        .then(setOrders)
        .finally(() => setLoading(false));
    }
  }, [user]);

  const handleCreateOrder = async (e) => {
    e.preventDefault();
    const result = await api.createOrder({ userId: user.id, ...newOrder });
    if (result.success) {
      // ÐŸÐ¾ÐºÐ°Ð·Ð°Ñ‚ÑŒ ÑƒÐ²ÐµÐ´Ð¾Ð¼Ð»ÐµÐ½Ð¸Ðµ Ð¾Ð± ÑƒÑÐ¿ÐµÑ…Ðµ
      showToast('âœ… Ð—Ð°ÑÐ²ÐºÐ° ÑƒÑÐ¿ÐµÑˆÐ½Ð¾ ÑÐ¾Ð·Ð´Ð°Ð½Ð°!');
      setShowForm(false);
      setFormStep(1);
      setLoading(true);
      api.getUserOrders(user.id).then(setOrders).finally(() => setLoading(false));
      
      // Ð¡Ð±Ñ€Ð¾Ñ Ñ„Ð¾Ñ€Ð¼Ñ‹
      setNewOrder({
        address: '', 
        contactPhone: '', 
        serviceDate: '', 
        serviceTime: '10:00',
        serviceType: 'Ð¾Ð±Ñ‰Ð¸Ð¹ ÐºÐ»Ð¸Ð½Ð¸Ð½Ð³', 
        customService: '', 
        paymentType: 'Ð½Ð°Ð»Ð¸Ñ‡Ð½Ñ‹Ðµ',
        specialRequests: ''
      });
    } else {
      showToast('âŒ ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ ÑÐ¾Ð·Ð´Ð°Ð½Ð¸Ð¸ Ð·Ð°ÑÐ²ÐºÐ¸', 'error');
    }
  };

  const showToast = (message, type = 'success') => {
    const toast = document.createElement('div');
    toast.className = `toast ${type}`;
    toast.textContent = message;
    document.body.appendChild(toast);
    
    setTimeout(() => {
      toast.remove();
    }, 3000);
  };

  const nextStep = () => {
    if (formStep === 1) {
      if (!newOrder.address || !newOrder.contactPhone || !newOrder.serviceDate) {
        showToast('Ð—Ð°Ð¿Ð¾Ð»Ð½Ð¸Ñ‚Ðµ Ð²ÑÐµ Ð¾Ð±ÑÐ·Ð°Ñ‚ÐµÐ»ÑŒÐ½Ñ‹Ðµ Ð¿Ð¾Ð»Ñ', 'error');
        return;
      }
    }
    if (formStep === 2 && newOrder.serviceType === 'Ð´Ñ€ÑƒÐ³Ð¾Ðµ' && !newOrder.customService) {
      showToast('ÐžÐ¿Ð¸ÑˆÐ¸Ñ‚Ðµ ÑƒÑÐ»ÑƒÐ³Ñƒ', 'error');
      return;
    }
    setFormStep(formStep + 1);
  };

  const prevStep = () => {
    setFormStep(formStep - 1);
  };

  const calculateEstimatedPrice = () => {
    const prices = {
      'Ð¾Ð±Ñ‰Ð¸Ð¹ ÐºÐ»Ð¸Ð½Ð¸Ð½Ð³': 'Ð¾Ñ‚ 2 000 â‚½',
      'Ð³ÐµÐ½ÐµÑ€Ð°Ð»ÑŒÐ½Ð°Ñ ÑƒÐ±Ð¾Ñ€ÐºÐ°': 'Ð¾Ñ‚ 4 500 â‚½',
      'Ð¿Ð¾ÑÐ»ÐµÑÑ‚Ñ€Ð¾Ð¸Ñ‚ÐµÐ»ÑŒÐ½Ð°Ñ ÑƒÐ±Ð¾Ñ€ÐºÐ°': 'Ð¾Ñ‚ 6 000 â‚½',
      'Ñ…Ð¸Ð¼Ñ‡Ð¸ÑÑ‚ÐºÐ° ÐºÐ¾Ð²Ñ€Ð¾Ð² Ð¸ Ð¼ÐµÐ±ÐµÐ»Ð¸': 'Ð¾Ñ‚ 3 500 â‚½',
      'Ð¼Ð¾Ð¹ÐºÐ° Ð¾ÐºÐ¾Ð½': 'Ð¾Ñ‚ 2 500 â‚½',
      'Ð´Ñ€ÑƒÐ³Ð¾Ðµ': 'Ð¿Ð¾ Ð´Ð¾Ð³Ð¾Ð²Ð¾Ñ€Ñ‘Ð½Ð½Ð¾ÑÑ‚Ð¸'
    };
    return prices[newOrder.serviceType] || 'Ð¿Ð¾ Ð´Ð¾Ð³Ð¾Ð²Ð¾Ñ€Ñ‘Ð½Ð½Ð¾ÑÑ‚Ð¸';
  };

  if (!user) return <Navigate to="/login" />;
  return (
    <div className="orders-page">

      {/* Ð¨Ð°Ð¿ÐºÐ° ÑÑ‚Ñ€Ð°Ð½Ð¸Ñ†Ñ‹ Ñ ÐºÐ½Ð¾Ð¿ÐºÐ¾Ð¹ "ÐÐ° Ð³Ð»Ð°Ð²Ð½ÑƒÑŽ" */}
      <div className="page-header-with-nav">
        <div className="header-left">
          <Link to="/" className="btn-home">
            <span className="home-icon">ðŸ </span>
            ÐÐ° Ð³Ð»Ð°Ð²Ð½ÑƒÑŽ
          </Link>
        </div>
        <div className="header-center">
          <h2>ÐœÐ¾Ð¸ Ð·Ð°ÑÐ²ÐºÐ¸</h2>
          <p className="page-subtitle">Ð£Ð¿Ñ€Ð°Ð²Ð»ÑÐ¹Ñ‚Ðµ ÑÐ²Ð¾Ð¸Ð¼Ð¸ Ð·Ð°ÑÐ²ÐºÐ°Ð¼Ð¸ Ð½Ð° ÑƒÐ±Ð¾Ñ€ÐºÑƒ</p>
        </div>
        <div className="header-right">
          <span className="user-greeting">ðŸ‘‹ ÐŸÑ€Ð¸Ð²ÐµÑ‚, {user.name}!</span>
        </div>
      </div>
      
      {/* ÐšÐ½Ð¾Ð¿ÐºÐ° ÑÐ¾Ð·Ð´Ð°Ð½Ð¸Ñ Ð·Ð°ÑÐ²ÐºÐ¸ */}
      <button 
        className="btn-create-order" 
        onClick={() => setShowForm(true)}
      >
        <span className="btn-icon">+</span>
        Ð¡Ð¾Ð·Ð´Ð°Ñ‚ÑŒ Ð½Ð¾Ð²ÑƒÑŽ Ð·Ð°ÑÐ²ÐºÑƒ
      </button>

      {/* ÐœÐ¾Ð´Ð°Ð»ÑŒÐ½Ð¾Ðµ Ð¾ÐºÐ½Ð¾ ÑÐ¾Ð·Ð´Ð°Ð½Ð¸Ñ Ð·Ð°ÑÐ²ÐºÐ¸ */}
      {showForm && (
        <div className="modal">
          <div className="modal-content order-form-modal">
            <div className="modal-header">
              <h3>ÐÐ¾Ð²Ð°Ñ Ð·Ð°ÑÐ²ÐºÐ° Ð½Ð° ÑƒÐ±Ð¾Ñ€ÐºÑƒ</h3>
              <button className="close-btn" onClick={() => { setShowForm(false); setFormStep(1); }}>Ã—</button>
            </div>
            
            <div className="form-progress">
              <div className="progress-bar" style={{ width: `${(formStep / 3) * 100}%` }}></div>
              <div className="progress-steps">
                <div className={`step ${formStep >= 1 ? 'active' : ''}`}>
                  <span className="step-number">1</span>
                  <span className="step-label">Ð”Ð°Ð½Ð½Ñ‹Ðµ</span>
                </div>
                <div className={`step ${formStep >= 2 ? 'active' : ''}`}>
                  <span className="step-number">2</span>
                  <span className="step-label">Ð£ÑÐ»ÑƒÐ³Ð°</span>
                </div>
                <div className={`step ${formStep >= 3 ? 'active' : ''}`}>
                  <span className="step-number">3</span>
                  <span className="step-label">ÐŸÐ¾Ð´Ñ‚Ð²ÐµÑ€Ð¶Ð´ÐµÐ½Ð¸Ðµ</span>
                </div>
              </div>
            </div>
            
            <form onSubmit={handleCreateOrder} className="order-form">
              {formStep === 1 && (
                <div className="form-step active">
                  <div className="step-header">
                    <h4>ÐšÐ¾Ð½Ñ‚Ð°ÐºÑ‚Ð½Ð°Ñ Ð¸Ð½Ñ„Ð¾Ñ€Ð¼Ð°Ñ†Ð¸Ñ</h4>
                    <p>Ð£ÐºÐ°Ð¶Ð¸Ñ‚Ðµ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ð´Ð»Ñ ÑÐ²ÑÐ·Ð¸ Ð¸ Ð°Ð´Ñ€ÐµÑ ÑƒÐ±Ð¾Ñ€ÐºÐ¸</p>
                  </div>
                  
                  <div className="form-group">
                    <label>
                      <span className="label-icon">ðŸ“</span>
                      ÐÐ´Ñ€ÐµÑ ÑƒÐ±Ð¾Ñ€ÐºÐ¸ *
                    </label>
                    <input 
                      type="text" 
                      placeholder="Ð’Ð²ÐµÐ´Ð¸Ñ‚Ðµ Ð¿Ð¾Ð»Ð½Ñ‹Ð¹ Ð°Ð´Ñ€ÐµÑ" 
                      required
                      value={newOrder.address} 
                      onChange={e => setNewOrder({...newOrder, address: e.target.value})} 
                    />
                  </div>
                  
                  <div className="form-group">
                    <label>
                      <span className="label-icon">ðŸ“±</span>
                      ÐšÐ¾Ð½Ñ‚Ð°ÐºÑ‚Ð½Ñ‹Ð¹ Ñ‚ÐµÐ»ÐµÑ„Ð¾Ð½ *
                    </label>
                    <input 
                      type="tel" 
                      placeholder="+7(XXX)-XXX-XX-XX" 
                      required
                      value={newOrder.contactPhone} 
                      onChange={e => setNewOrder({...newOrder, contactPhone: e.target.value})} 
                    />
                  </div>
                  
                  <div className="form-row">
                    <div className="form-group">
                      <label>
                        <span className="label-icon">ðŸ“…</span>
                        Ð”Ð°Ñ‚Ð° ÑƒÐ±Ð¾Ñ€ÐºÐ¸ *
                      </label>
                      <input 
                        type="date" 
                        required 
                        min={new Date().toISOString().split('T')[0]}
                        value={newOrder.serviceDate}
                        onChange={e => setNewOrder({...newOrder, serviceDate: e.target.value})} 
                      />
                    </div>
                    
                    <div className="form-group">
                      <label>
                        <span className="label-icon">â°</span>
                        Ð’Ñ€ÐµÐ¼Ñ
                      </label>
                      <select 
                        value={newOrder.serviceTime}
                        onChange={e => setNewOrder({...newOrder, serviceTime: e.target.value})}
                      >
                        {timeSlots.map(time => (
                          <option key={time} value={time}>{time}</option>
                        ))}
                      </select>
                    </div>
                  </div>
                  
                  <div className="form-actions">
                    <button type="button" className="btn-next" onClick={nextStep}>
                      Ð”Ð°Ð»ÐµÐµ
                      <span className="arrow">â†’</span>
                    </button>
                  </div>
                </div>
              )}
              
              {formStep === 2 && (
                <div className="form-step active">
                  <div className="step-header">
                    <h4>Ð’Ñ‹Ð±Ð¾Ñ€ ÑƒÑÐ»ÑƒÐ³Ð¸</h4>
                    <p>Ð’Ñ‹Ð±ÐµÑ€Ð¸Ñ‚Ðµ Ñ‚Ð¸Ð¿ ÑƒÐ±Ð¾Ñ€ÐºÐ¸ Ð¸ Ð´Ð¾Ð¿Ð¾Ð»Ð½Ð¸Ñ‚ÐµÐ»ÑŒÐ½Ñ‹Ðµ Ð¾Ð¿Ñ†Ð¸Ð¸</p>
                  </div>
                  
