import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, Link, Navigate } from 'react-router-dom';
import Logo from './logo';

// API –∫–ª–∏–µ–Ω—Ç
const api = {
  register: (data) => fetch('/api/register', {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(data)
  }).then(res => res.json()),

  login: (data) => fetch('/api/login', {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(data)
  }).then(res => res.json()),

  createOrder: (data) => fetch('/api/orders', {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(data)
  }).then(res => res.json()),

  getUserOrders: (userId) => fetch(`/api/orders/${userId}`).then(res => res.json()),

  getAdminOrders: () => fetch('/api/admin/orders').then(res => res.json()),

  updateOrderStatus: (id, data) => fetch(`/api/admin/orders/${id}`, {
    method: 'PUT',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(data)
  }).then(res => res.json())
};

// –ö–æ–º–ø–æ–Ω–µ–Ω—Ç —Å–ª–∞–π–¥–µ—Ä–∞
const ImageSlider = () => {
  const images = ['/images/clean1.jpg', '/images/clean2.jpg', '/images/clean3.jpg', '/images/clean4.jpg', '/images/clean5.jpg'];
  const [current, setCurrent] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrent(prev => (prev + 1) % images.length);
    }, 3000);
    return () => clearInterval(timer);
  }, [images.length]);

  return (
    <div className="slider">
      <button className="slider-btn prev" onClick={() => setCurrent(prev => (prev - 1 + images.length) % images.length)}>‚Äπ</button>
      <img src={images[current]} alt={`–£–±–æ—Ä–∫–∞ ${current + 1}`} />
      <button className="slider-btn next" onClick={() => setCurrent(prev => (prev + 1) % images.length)}>‚Ä∫</button>
      <div className="slider-dots">
        {images.map((_, i) => (
          <span key={i} className={`dot ${i === current ? 'active' : ''}`} onClick={() => setCurrent(i)}></span>
        ))}
      </div>
    </div>
  );
};

// –ì–ª–∞–≤–Ω–∞—è —Å—Ç—Ä–∞–Ω–∏—Ü–∞
const HomePage = () => {
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user')));

  return (
    <div>
      <header>

        <Logo size="medium" showText={true} />



        <nav>
          {user ? (
            <>
              <span>–î–æ–±—Ä–æ –ø–æ–∂–∞–ª–æ–≤–∞—Ç—å, {user.name}</span>
              <Link to="/orders">–ú–æ–∏ –∑–∞—è–≤–∫–∏</Link>
              {user.login === 'adminka' && <Link to="/admin">–ü–∞–Ω–µ–ª—å –∞–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä–∞</Link>}
              <button onClick={() => { localStorage.removeItem('user'); window.location.href = '/'; }}>–í—ã–π—Ç–∏</button>
            </>
          ) : (
            <>
              <Link to="/login">–í—Ö–æ–¥</Link>
              <Link to="/register">–†–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏—è</Link>
            </>
          )}
        </nav>
      </header>

      <main>
        <section className="hero">
          <h1>–ü—Ä–æ—Ñ–µ—Å—Å–∏–æ–Ω–∞–ª—å–Ω—ã–µ –∫–ª–∏–Ω–∏–Ω–≥–æ–≤—ã–µ —É—Å–ª—É–≥–∏</h1>
          <p>–ö–∞—á–µ—Å—Ç–≤–µ–Ω–Ω–∞—è —É–±–æ—Ä–∫–∞ –∂–∏–ª—ã—Ö –∏ –ø—Ä–æ–∏–∑–≤–æ–¥—Å—Ç–≤–µ–Ω–Ω—ã—Ö –ø–æ–º–µ—â–µ–Ω–∏–π</p>
        </section>

        <section className="slider-section">
          <ImageSlider />
        </section>


      </main>
    </div>
  );
};

// –†–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏—è
const RegisterPage = () => {
  const [form, setForm] = useState({ login: '', password: '', full_name: '', phone: '', email: '' });
  const [errors, setErrors] = useState({});

  const validate = () => {
    const newErrors = {};
    if (!form.login) newErrors.login = '–û–±—è–∑–∞—Ç–µ–ª—å–Ω–æ–µ –ø–æ–ª–µ';
    if (form.password.length < 6) newErrors.password = '–ú–∏–Ω–∏–º—É–º 6 —Å–∏–º–≤–æ–ª–æ–≤';
    if (!/^[–ê-–Ø–∞-—è—ë–Å\s]+$/.test(form.full_name)) newErrors.full_name = '–¢–æ–ª—å–∫–æ –∫–∏—Ä–∏–ª–ª–∏—Ü–∞ –∏ –ø—Ä–æ–±–µ–ª—ã';
    if (!/^\+7\(\d{3}\)-\d{3}-\d{2}-\d{2}$/.test(form.phone)) newErrors.phone = '–§–æ—Ä–º–∞—Ç: +7(XXX)-XXX-XX-XX';
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(form.email)) newErrors.email = '–ù–µ–≤–µ—Ä–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç email';
    return newErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const validationErrors = validate();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    const result = await api.register(form);
    if (result.success) {
      alert('–†–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏—è —É—Å–ø–µ—à–Ω–∞!');
      window.location.href = '/login';
    } else {
      setErrors({ submit: result.error });
    }
  };

  return (
    <div className="auth-page">
      <h2>–†–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏—è</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="–õ–æ–≥–∏–Ω" value={form.login} 
          onChange={e => setForm({...form, login: e.target.value})} />
        {errors.login && <span className="error">{errors.login}</span>}

        <input type="password" placeholder="–ü–∞—Ä–æ–ª—å" value={form.password}
          onChange={e => setForm({...form, password: e.target.value})} />
        {errors.password && <span className="error">{errors.password}</span>}

        <input type="text" placeholder="–§–ò–û" value={form.full_name}
          onChange={e => setForm({...form, full_name: e.target.value})} />
        {errors.full_name && <span className="error">{errors.full_name}</span>}

        <input type="text" placeholder="–¢–µ–ª–µ—Ñ–æ–Ω" value={form.phone}
          onChange={e => setForm({...form, phone: e.target.value})} />
        {errors.phone && <span className="error">{errors.phone}</span>}

        <input type="email" placeholder="Email" value={form.email}
          onChange={e => setForm({...form, email: e.target.value})} />
        {errors.email && <span className="error">{errors.email}</span>}

        {errors.submit && <div className="error">{errors.submit}</div>}

        <button type="submit">–ó–∞—Ä–µ–≥–∏—Å—Ç—Ä–∏—Ä–æ–≤–∞—Ç—å—Å—è</button>
      </form>
      <Link to="/login">–£–∂–µ –µ—Å—Ç—å –∞–∫–∫–∞—É–Ω—Ç? –í–æ–π—Ç–∏</Link>
    </div>
  );
};

// –í—Ö–æ–¥
const LoginPage = () => {
  const [form, setForm] = useState({ login: '', password: '' });
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const result = await api.login(form);
    if (result.success) {
      localStorage.setItem('user', JSON.stringify(result.user));
      window.location.href = result.isAdmin ? '/admin' : '/orders';
    } else {
      setError(result.error);
    }
  };

  return (
    <div className="auth-page">
      <h2>–í—Ö–æ–¥</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="–õ–æ–≥–∏–Ω" value={form.login}
          onChange={e => setForm({...form, login: e.target.value})} />
        <input type="password" placeholder="–ü–∞—Ä–æ–ª—å" value={form.password}
          onChange={e => setForm({...form, password: e.target.value})} />
        {error && <div className="error">{error}</div>}
        <button type="submit">–í–æ–π—Ç–∏</button>
      </form>
      <Link to="/register">–ù–µ—Ç –∞–∫–∫–∞—É–Ω—Ç–∞? –ó–∞—Ä–µ–≥–∏—Å—Ç—Ä–∏—Ä—É–π—Ç–µ—Å—å</Link>
    </div>
  );
};

// –°–æ–∑–¥–∞–Ω–∏–µ –∑–∞—è–≤–∫–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
const OrdersPage = () => {
  const [user] = useState(() => {
    try {
      return JSON.parse(localStorage.getItem('user'));
    } catch {
      return null;
    }
  });

  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [formStep, setFormStep] = useState(1);
  const [newOrder, setNewOrder] = useState({
    address: '–ê–¥—Ä–µ—Å—Å—Å—Å', 
    contactPhone: '+7(912)-111-11-11', 
    serviceDate: '', 
    serviceTime: '10:00',
    serviceType: '–æ–±—â–∏–π –∫–ª–∏–Ω–∏–Ω–≥', 
    customService: '', 
    paymentType: '–Ω–∞–ª–∏—á–Ω—ã–µ',
    specialRequests: ''
  });

  const serviceOptions = [
    { value: '–æ–±—â–∏–π –∫–ª–∏–Ω–∏–Ω–≥', label: 'üßπ –û–±—â–∏–π –∫–ª–∏–Ω–∏–Ω–≥', description: '–ï–∂–µ–¥–Ω–µ–≤–Ω–∞—è —É–±–æ—Ä–∫–∞ –ø–æ–º–µ—â–µ–Ω–∏–π' },
    { value: '–≥–µ–Ω–µ—Ä–∞–ª—å–Ω–∞—è —É–±–æ—Ä–∫–∞', label: '‚ú® –ì–µ–Ω–µ—Ä–∞–ª—å–Ω–∞—è —É–±–æ—Ä–∫–∞', description: '–ü–æ–ª–Ω–∞—è –∫–æ–º–ø–ª–µ–∫—Å–Ω–∞—è —É–±–æ—Ä–∫–∞' },
    { value: '–ø–æ—Å–ª–µ—Å—Ç—Ä–æ–∏—Ç–µ–ª—å–Ω–∞—è —É–±–æ—Ä–∫–∞', label: 'üèóÔ∏è –ü–æ—Å–ª–µ—Å—Ç—Ä–æ–∏—Ç–µ–ª—å–Ω–∞—è —É–±–æ—Ä–∫–∞', description: '–£–±–æ—Ä–∫–∞ –ø–æ—Å–ª–µ —Ä–µ–º–æ–Ω—Ç–∞' },
    { value: '—Ö–∏–º—á–∏—Å—Ç–∫–∞ –∫–æ–≤—Ä–æ–≤ –∏ –º–µ–±–µ–ª–∏', label: 'üßº –•–∏–º—á–∏—Å—Ç–∫–∞ –∫–æ–≤—Ä–æ–≤ –∏ –º–µ–±–µ–ª–∏', description: '–ü—Ä–æ—Ñ–µ—Å—Å–∏–æ–Ω–∞–ª—å–Ω–∞—è —á–∏—Å—Ç–∫–∞' },
    { value: '–º–æ–π–∫–∞ –æ–∫–æ–Ω', label: 'ü™ü –ú–æ–π–∫–∞ –æ–∫–æ–Ω', description: '–ß–∏—Å—Ç–∫–∞ –æ–∫–æ–Ω –∏ –≤–∏—Ç—Ä–∏–Ω' },
    { value: '–¥—Ä—É–≥–æ–µ', label: 'üéØ –î—Ä—É–≥–∞—è —É—Å–ª—É–≥–∞', description: '–°–ø–µ—Ü–∏–∞–ª—å–Ω—ã–µ —É—Å–ª—É–≥–∏' }
  ];

  const timeSlots = [
    '08:00', '09:00', '10:00', '11:00', '12:00', 
    '13:00', '14:00', '15:00', '16:00', '17:00', '18:00'
  ];

  useEffect(() => {
    if (user && user.id) {
      setLoading(true);
      api.getUserOrders(user.id)
        .then(setOrders)
        .finally(() => setLoading(false));
    }
  }, [user]);

  const handleCreateOrder = async (e) => {
    e.preventDefault();
    const result = await api.createOrder({ userId: user.id, ...newOrder });
    if (result.success) {
      // –ü–æ–∫–∞–∑–∞—Ç—å —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–µ –æ–± —É—Å–ø–µ—Ö–µ
      showToast('‚úÖ –ó–∞—è–≤–∫–∞ —É—Å–ø–µ—à–Ω–æ —Å–æ–∑–¥–∞–Ω–∞!');
      setShowForm(false);
      setFormStep(1);
      setLoading(true);
      api.getUserOrders(user.id).then(setOrders).finally(() => setLoading(false));

      // –°–±—Ä–æ—Å —Ñ–æ—Ä–º—ã
      setNewOrder({
        address: '', 
        contactPhone: '', 
        serviceDate: '', 
        serviceTime: '10:00',
        serviceType: '–æ–±—â–∏–π –∫–ª–∏–Ω–∏–Ω–≥', 
        customService: '', 
        paymentType: '–Ω–∞–ª–∏—á–Ω—ã–µ',
        specialRequests: ''
      });
    } else {
      showToast('‚ùå –û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ–∑–¥–∞–Ω–∏–∏ –∑–∞—è–≤–∫–∏', 'error');
    }
  };

  const showToast = (message, type = 'success') => {
    const toast = document.createElement('div');
    toast.className = `toast ${type}`;
    toast.textContent = message;
    document.body.appendChild(toast);

    setTimeout(() => {
      toast.remove();
    }, 3000);
  };

  const nextStep = () => {
